import React, { useState, useRef } from 'react';

function LocatorHelper() {
    const [url, setUrl] = useState('https://www.google.com'); // Default URL for demonstration
    const [locators, setLocators] = useState({});
    const [error, setError] = useState('');
    const iframeRef = useRef(null);

    const handleLoadUrl = async () => {
        if (!url) {
            setError('Please enter a valid URL.');
            return;
        }
        setError('');
        try {
            const response = await fetch(`http://localhost:4000/api/proxy?url=${encodeURIComponent(url)}`);
            if (!response.ok) {
                throw new Error(`Failed to fetch URL: ${response.statusText}`);
            }
            const html = await response.text();
            const iframeDocument = iframeRef.current.contentDocument;
            if (iframeDocument) {
                iframeDocument.open();
                iframeDocument.write(html);
                iframeDocument.close();
            } else {
                setError("Could not write to iframe.");
            }
        } catch (e) {
            setError(`Error loading site: ${e.message}`);
            console.error(e);
        }
    };

    const handleIframeLoad = () => {
        try {
            const iframeDocument = iframeRef.current.contentDocument;
            if (!iframeDocument) {
                setError("Could not access iframe content. This might be due to CORS policy.");
                return;
            }
            iframeDocument.addEventListener('click', (e) => {
                e.preventDefault();
                e.stopPropagation();
                const target = e.target;
                const generatedLocators = generateLocators(target);
                setLocators(generatedLocators);
            }, true); // Use capturing to get the event first
        } catch (e) {
            setError(`Error accessing iframe content: ${e.message}. Try a different URL or check browser console.`);
            console.error(e);
        }
    };

    const generateLocators = (element) => {
        const locators = {};

        // ID
        if (element.id) {
            locators.id = element.id;
        }

        // Name
        if (element.name) {
            locators.name = element.name;
        }

        // Link Text
        if (element.tagName === 'A' && element.textContent) {
            locators.linkText = element.textContent;
        }

        // Partial Link Text
        if (element.tagName === 'A' && element.textContent) {
            locators.partialLinkText = element.textContent.split(' ')[0];
        }

        // CSS Selector
        locators.css = generateCssSelector(element);

        // XPath
        locators.xpath = generateXPath(element);

        // Text
        if (element.textContent) {
            locators.text = element.textContent;
        }

        return locators;
    };

    const generateCssSelector = (element) => {
        if (element.id) return `#${element.id}`;

        let path = '';
        let current = element;
        while (current.parentElement) {
            let siblingIndex = 1;
            let sibling = current.previousElementSibling;
            while (sibling) {
                if (sibling.tagName === current.tagName) {
                    siblingIndex++;
                }
                sibling = sibling.previousElementSibling;
            }
            const tagName = current.tagName.toLowerCase();
            const nthChild = `:nth-child(${siblingIndex})`;
            path = ` > ${tagName}${nthChild}` + path;
            current = current.parentElement;
            if (current.tagName.toLowerCase() === 'body') break; // Stop at body
        }
        return `body${path}`.trim();
    };

    const generateXPath = (element) => {
        if (element.id) return `//*[@id='${element.id}']`;

        let path = '';
        let current = element;
        while (current.parentElement) {
            let siblingIndex = 1;
            let sibling = current.previousElementSibling;
            while (sibling) {
                if (sibling.tagName === current.tagName) {
                    siblingIndex++;
                }
                sibling = sibling.previousElementSibling;
            }
            const tagName = current.tagName.toLowerCase();
            path = `/${tagName}[${siblingIndex}]` + path;
            current = current.parentElement;
        }
        return `/html/body${path}`;
    };

    const copyToClipboard = (text) => {
        if (text) {
            navigator.clipboard.writeText(text)
                .then(() => alert('Locator copied to clipboard!'))
                .catch(err => console.error('Failed to copy: ', err));
        }
    };

    return (
        <div className="bg-white p-6 rounded-lg shadow-lg w-full">
            <h3 className="text-xl font-bold text-gray-800 mb-4">Locator Helper</h3>
            <div className="flex items-center gap-2 mb-4">
                <input
                    type="url" // Use type="url" for better semantics
                    value={url}
                    onChange={(e) => setUrl(e.target.value)}
                    className="flex-grow p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-purple-500 focus:border-transparent transition"
                    placeholder="Enter URL to inspect (e.g., https://example.com)"
                />
                <button
                    onClick={handleLoadUrl}
                    className="bg-purple-500 text-white p-3 rounded-lg hover:bg-purple-600 transition-colors duration-300 shadow-md"
                >
                    Load Site
                </button>
            </div>

            {error && <p className="text-red-500 text-sm mb-4">{error}</p>}

            {Object.keys(locators).length > 0 && (
                <div className="mb-4">
                    <label className="block text-md font-semibold text-gray-700 mb-2">Generated Locators:</label>
                    <div className="space-y-2">
                        {Object.entries(locators).map(([key, value]) => (
                            <div key={key} className="flex items-center gap-2">
                                <span className="font-semibold text-gray-600 capitalize">{key}:</span>
                                <pre className="flex-grow bg-gray-100 p-3 rounded-lg text-gray-800 font-mono text-sm overflow-x-auto">{value}</pre>
                                <button
                                    onClick={() => copyToClipboard(value)}
                                    className="bg-gray-200 p-2 rounded-lg hover:bg-gray-300 transition-colors"
                                    title="Copy to clipboard"
                                >
                                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5 text-gray-600" viewBox="0 0 20 20" fill="currentColor"><path d="M8 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z" /><path d="M6 3a2 2 0 00-2 2v11a2 2 0 002 2h8a2 2 0 002-2V5a2 2 0 00-2-2H6zm8 2H6v11h8V5z" /></svg>
                                </button>
                            </div>
                        ))}
                    </div>
                </div>
            )}

            <div className="w-full h-96 border-2 border-gray-300 rounded-lg overflow-hidden bg-gray-100">
                <iframe
                    ref={iframeRef}
                    onLoad={handleIframeLoad}
                    className="w-full h-full border-0"
                    title="Locator Helper Inspection Frame"
                    sandbox="allow-scripts allow-same-origin" // Security sandbox
                ></iframe>
            </div>
            <p className="text-xs text-gray-500 mt-2">Click on any element in the frame above to generate its locators.</p>
        </div>
    );
}

export default LocatorHelper;
